script:
frappe.ui.form.on("Stock Entry", {
    setup: function(frm) {
        erpnext.is_perpetual_inventory_enabled = function(){
            return true
        }
    }
})

frappe.ui.form.on("BOM", {
    refresh: function(frm) {
        frm.set_query("item_code", "items", function(frm, cdt, cdn) {
            var row = frappe.get_doc(cdt, cdn)
            var filters = {
                "has_variants": "0",
                "variant_of": row.template
            }
            return {
                "filters": filters
            }
        })

        frm.set_query("template", "items", function(frm, cdt, cdn) {
            var row = frappe.get_doc(cdt, cdn)
            var filters = {
                "has_variants": "1"
            }
            return {
                "filters": filters
            }
        })
        frappe.db.get_value("Stock Settings", "Stock Settings", "item_group", function(data) {
            frm.set_query("item", function() {
                return {
                    "filters": {
                        "item_group": data.item_group
                    }
                }
            })
        })
    },
    onload: function(frm) {
        if (frm.doc.__islocal) {
            $.each(frm.doc.items, function(idx, row) {
                frappe.model.set_value(row.doctype, row.docname, 'item_code', '')
            })
            refresh_field("items")
        }
    }
})

frappe.ui.form.on("Item", {
    refresh: function(frm) {
        if (!frm.doc.__islocal) {
            // frm.trigger("hide_checks")
        }
    },
    onload_post_render: function(frm) {
        if (frm.doc.__islocal && frm.doc.item_group == 'Materiales') {
            frm.trigger("fill_tec_table")
        }
    },
    fill_tec_table: function(frm) {
        if (true) {
            var method = "frappe.client.get_list"
            var args = {
                "doctype": "Tecnologia de Impresion",
                "fields": [
                    "name"
                ],
                "filters": {
                    "enabled": "1"
                },
                "limit_page_length": 0 //unset the limits
            }
            var callback = function(response) {
                var dimension_list = response.message

                // clear the table
                frm.set_value("tecnologias", [])

                // the fill it
                $.each(dimension_list, function(idx, tecnologia) {
                    frm.add_child("tecnologias", {
                        "checked": 0,
                        "technology": tecnologia.name
                    })
                })

                refresh_field("tecnologias")
                // frm.trigger("hide_checks")
            }

            frappe.call({
                "method": method,
                "args": args,
                "callback": callback
            })
        }
    },
    item_group: function(frm) {
        if (frm.doc.item_group == "Materiales") {
            frm.trigger("fill_tec_table")
        }
    },
    hide_checks: function(frm) {
        $(document)
            .find("[data-page-route='Form/Item']")
            .find("[data-fieldname=tecnologias]")
            .find(".grid-row-check.pull-left")
            .css("display", "none")

        $(document)
            .find("[data-page-route='Form/Item']")
            .find(".small.form-clickable-section.grid-footer")
            .css("display", "none")
    }
})
